def get_lighting_data(location_name):
    """
    Retrieve hospital lighting data based on location name.
    Performs a normalized lookup.
    """
    import re
    
    # Normalize input
    key = re.sub(r'[*_]', '', location_name).strip().lower()
    key = re.sub(r'\\s+', ' ', key)
    
    return LIGHTING_HOSPITAL_DATA.get(key)